vertexShader.code:

#version 120

uniform mat4 worldViewProjection;

attribute vec4 vertexPosition;

void main() {
  gl_Position = worldViewProjection * vertexPosition;
}

fragmentShader.code:

#version 120

uniform int type;

void main() {
  if (type == 1)
    gl_FragColor = vec4(0.25, 0.25, 0.25, 1);
  else if (type == 2)
    gl_FragColor = vec4(0.5, 0.5, 0, 1);
  else
    gl_FragColor = vec4(1, 1, 1, 1);
}
